import React from 'react';
import {useState, useEffect} from 'react';
import styled from 'styled-components';

const LatestPosts: React.FC = () =>{
    const [data, setData] = useState<any>(Array.from({length:3},(i, v)=>v));
    console.log(data);
    return(
        <PostsWrap>
            <PostsContainer>
                <PostsHeader>
                    <h1>Lastst Posts</h1>
                </PostsHeader>
                <PostsContent>
                    {data.map((item:any)=>
                    <article key={item}>
                        <div className="posts_wrap">
                            <div className="posts_thumb">

                            </div>
                        </div>
                    </article>)}
                </PostsContent>
            </PostsContainer>
        </PostsWrap>
    );
}

const PostsWrap = styled.div`
    width:1180px;
    margin: 0 auto;
    max-width:100%;
`;

const PostsContainer = styled.div`
     margin: 50px 0px 0px 0px;
`;

const PostsHeader = styled.div`
    &>h1{
        font-size:28px;
        font-weight:400;
    }
`;

const PostsContent = styled.div`
    margin:20px 0px 0px 0px;
    display:flex;
    justify-content:space-between;
    &>article{
        width:30%;
        border:1px solid;
        cursor: pointer;
        &>.posts_wrap{
            width:100%;
            position: relative;
            padding-bottom:100%;
            &>.posts_thumb{
               position: absolute;
               top:0;
            }
        }
    }
`;
export default LatestPosts;
