import React from 'react';
import styled from 'styled-components'; 
import 'codemirror/lib/codemirror.css';
import '@toast-ui/editor/dist/toastui-editor.css';
import { EditorProps } from '@toast-ui/react-editor';
import dynamic from 'next/dynamic';
import colorSyntaxPlugin from '@toast-ui/editor-plugin-color-syntax'
import codeSyntaxHighlightPlugin from '@toast-ui/editor-plugin-code-syntax-highlight'
import hljs from 'highlight.js'
import "highlight.js/styles/github.css";

const Editor = dynamic<EditorProps>(() => import('@toast-ui/react-editor').then(m => m.Editor), { ssr: false });

const BlogCreateComponent: React.FC = () =>{
    return(
        <BlogCreateWrap>
              <Editor
                    usageStatistics={false}
                    useCommandShortcut={true}
                    previewStyle="vertical"
                    height="400px"
                    initialEditType="markdown"
                    initialValue="hello"
                    plugins={[[codeSyntaxHighlightPlugin,{hljs}],[colorSyntaxPlugin]]}
                />
        </BlogCreateWrap>
    );
}

const BlogCreateWrap = styled.div`
    height:100vh;
`;

export default BlogCreateComponent;
