import React from 'react';
import {useState} from 'react';
import styled from 'styled-components';
import Link from 'next/link';
const JoinComponent: React.FC = () =>{
    const [email, setEmail] = useState<string>('');
    const [password, setPassword] = useState<string>('');
    const [passwordConfirm, setPasswordConfirm] = useState<string>('');
    const [name, setName] = useState<string>('');
    const [phone, setPhone] = useState<string>('');

    const onChangeHandler = (e:any) =>{
        const {id, value} = e.target;
        switch(id){
            case 'email' :
                return setEmail(value);
            case 'password' :
                return setPassword(value);
            case 'passwordConfirm' :
                return setPasswordConfirm(value);
            case 'name' :
                return setName(value);
            case 'phone' :
                return setPhone(value);
        }
    }
    return(
        <JoinWrap>
            <JoinContainer>
                <JoinHeader>
                    <Link href="/">
                        <a>Choi Tech</a>
                    </Link>
                </JoinHeader>
                <JoinContent>
                    <h1>회원 정보를 입력해주세요.</h1>
                    <p>id or email</p>
                    <div>
                        <input type="text" placeholder="ID 또는 E-mail을 입력해주세요." value={email} id="email" onChange={onChangeHandler}/>
                        <button>중복확인</button>
                    </div>
                    <p>password</p>
                    <input type="text" placeholder="비밀번호를 입력해 주세요." value={password} id="password" onChange={onChangeHandler}/>
                    <p>password Confirm</p>
                    <input type="text" placeholder="비밀번호 확인" value={passwordConfirm} id="passwordConfirm" onChange={onChangeHandler}/>
                    <p>name</p>
                    <input type="text" placeholder="이름을 입력해 주세요." value={name} id="name" onChange={onChangeHandler}/>
                    <p>핸드폰번호</p>
                    <input type="text" placeholder="-를 제외한 핸드폰 번호를 입력해주세요." value={phone} id="phone" onChange={onChangeHandler}/>
                    <div className="user_join">
                        <button>LOGIN</button>
                    </div>
                </JoinContent>
                <LoginFooter>
                    <p>
                    Copyright © 2021 by Choi Tech, Inc. All rights reserved
                    </p>
                </LoginFooter>
            </JoinContainer>
        </JoinWrap>
    );
}

const JoinWrap = styled.div`
    
`;

const JoinContainer = styled.div`
    width:360px;
    margin: 150px auto;  
    &>h1{
        margin:0px 0px 20px 0px;
        font-size:26px;
    }
`;
const JoinHeader = styled.div`
    text-align:center;
    &>a{
        font-family: 'Audiowide',cursive;
        text-decoration:none;
        color:#333333;
        font-size:32px;
    }
`;

const JoinContent = styled.div`
    margin-top:30px;
    &>h1{
        font-size:16px;
        font-weight:400;
        text-align:center;
    }
    &>p{
        margin:10px 0px 5px 0px;
        font-size:10px;
    }
    &>div{
        display:flex;
        justify-content:space-between;
        &>input{
            padding:0 10px;
            width:calc(70% - 25px);
            height:45px;
            outline:none;
            font-size:12px;
            background:none;
            border-radius:5px;
            border:1px solid #333333;
        }
        &>button{
            width:25%;
            height:45px;
            cursor: pointer;
            outline:none;
            background-color:rgb(18, 184, 134);
            color:#fff;
            font-size:16px;
            font-weight:bold;
            border:none;
            border-radius:5px;
        }
    }
    &>input{
        width:calc(100% - 25px);
            height:50px;
            outline:none;
            padding:0 10px;
            margin-bottom:10px;
            font-size:12px;
            background:none;
            border-radius:5px;
            border:1px solid #333333;
    }
    &>.user_join{
        &>button{
            width:100%;
            height:45px;
            cursor: pointer;
            outline:none;
            background-color:rgb(18, 184, 134);
            color:#ffffff;
            font-size:16px;
            font-weight:bold;
            border:none;
            border-radius:5px;
        }
    }
`;
const LoginFooter = styled.div`
margin-top:30px;
    &>p{
        font-size:10px;
    }
`;
export default JoinComponent;
