import { observable, action } from 'mobx';
import client from '../../lib/client';

class TestStore {
	@observable test: string = 'mobx teststore';
	@observable
	apiData:any = [];

	@action
	getApiData = async() =>{
		const res = await client.get('/user');
		this.apiData = await res.data;
	}
}

export default TestStore;
