import React from 'react';
import styled from 'styled-components';
import Link from 'next/link';

const LoginComponent: React.FC = () =>{
    return(
        <LoginWrap>
            <LoginContainer>
                <LoginHeader>
                    <Link href="/">
                        <a>Choi Tech</a>
                    </Link>
                </LoginHeader>
                <LoginContent>
                    <div className="user-info">
                        <p>Email or Id</p>
                        <input type="text" />
                        <p>Password</p>
                        <input type="text" />
                    </div>
                    <Link href="/">
                        <a>Forgot Your Password?</a>
                    </Link>
                    <button>LOGIN</button>
                </LoginContent>
            </LoginContainer>
        </LoginWrap>
    );
}

const LoginWrap = styled.div`

`;

const LoginHeader = styled.div`
    text-align:center;
    &>a{
        font-family: 'Audiowide',cursive;
        text-decoration:none;
        color:#333333;
        font-size:32px;
    }
`;
const LoginContainer = styled.div`
    width:360px;
    margin: 150px auto;  
    &>h1{
        margin:0px 0px 20px 0px;
        font-size:26px;
    }
`;

const LoginContent = styled.div`
    margin:30px 0px 0px 0px;
    &>div{
        &>p{
            margin:0px 0px 5px 0px;
            font-size:12px;
        }
        &>input{
            width:calc(100% - 25px);
            height:50px;
            outline:none;
            padding:0 10px;
            margin-bottom:10px;
        }
    }
    &>a{
        text-decoration:none;
        color:#333333;
        
        right:0;
    }
    &>button{
        width:100%;
        height:40px;
        cursor: pointer;
        outline:none;
    }

`;
export default LoginComponent;
