import React from 'react';
import styled from 'styled-components';

const QuestionComponent: React.FC = () =>{
    const data = Array.from({length:15},(v,i)=>i);

    return(
        <QuestionWrap>
            <QuestionBackground style={{backgroundImage:`url(${'./images/questionBackground.png'})`}}>
                <div>
                    <h1>질문과답변</h1>
                    <p>개발자들이 공유한 경험 및 기술 외 적으로, 질의 응답을 수행 합니다.</p>
                </div>
            </QuestionBackground>
            <QuestionContainer>
                <div className="content_header">
                    <h1>Related Projects</h1>
                    <p>새 글 작성</p>
                </div>
                <QuestionContent>
                    {data.map((item)=>
                    <article key={item}>
                        <div className="content_wrap">
                            <div className="image">

                            </div>
                            <div className="info">
                                <p>작성날짜</p>
                                <p>title</p>
                                <p>주요기능</p>
                                <div>
                                    <p>userInfo</p>
                                </div>
                            </div>
                        </div>
                    </article>
                    )}
                </QuestionContent>
            </QuestionContainer>

        </QuestionWrap>
    );
}

const QuestionWrap = styled.div``;

const QuestionBackground = styled.div`
    height:500px;
    background-size:100% 500px;
    &>div{
        width:1180px;
        margin:0 auto;
        position: relative;
        padding-top:250px;
        &>h1{
            margin:0;
            color:#ffffff;
            font-size:42px;
        }
        &>p{
            margin:20px 0px 0px 0px;
            color:#ffffff;
            font-size:18px;
        }
    }
`;

const QuestionContainer = styled.div`
    width:1180px;
    margin:0 auto;
    max-width:100%;

    &>.content_header{
        margin-top:20px;
        display:flex;
        justify-content:space-between;
        &>h1{
            margin:0;
        }
        &>p{
            margin:auto 0px 0px 0px;
            cursor: pointer;
            font-size:14px;
            &:hover{
                font-weight:bold;
            }
        }
    }
`;  
const QuestionContent = styled.div`
    margin:30px 0px 100px 0px;
    display:flex;
    flex-wrap:wrap;
    justify-content:space-between;
    &>article{
        width:45%;
        border:1px solid;
        margin-bottom:20px;
        border-radius:10px;
        cursor: pointer;
        &>.content_wrap{
            position: relative;
            padding-bottom:70%;
            &>.image{
                position:absolute;
                top:0;
                left:0;
                right:0;
                bottom:50%;
                background-color:#e5e5e5;
                border-radius:10px 10px 0px 0px;
            }
            &>.info{
                padding:20px;
                position:absolute;
                top:50%;
                left:0;
                right:0;
                bottom:0;
                &>p{
                    margin:0;
                }
            }
        }
    }
`;

export default QuestionComponent;
