import React from 'react';
import styled from 'styled-components';

const ExplainSite: React.FC = () =>{
    return(
        <ExplainWrap>
            <ExplainContainer>
            <h1>Try the various services provided by Choi Tech.</h1>
            <ExplainContent>
                <div>
                    <h2>지식 공유</h2>
                    <p>Front-end 프레임워크인 React에 관련된 기술 및 경험을 공유 합니다. 주로 개발 환경 구성 및, </p>
                </div>
            </ExplainContent>
            </ExplainContainer>
        </ExplainWrap>
    );
}

const ExplainWrap = styled.div`
    width:1180px;
    max-width:100%;
    margin:0 auto;
`;

const ExplainContainer = styled.div`
    margin:50px 0px 0px 0px;
    &>h1{
        font-size:28px;
        font-weight:400;
    }
`;

const ExplainContent = styled.div`

`;
export default ExplainSite;
